                        msg += '    <strong class="font-bold">Success!</strong>';
                        msg += '    <span class="block sm:inline">' + res.success + '</span>';
                        msg += ' </div>';

                        $('#msg-bag').append(msg);

                        $(':input', 'form')
                            .not(':button, :submit, :reset, :hidden')
                            .val('')
                            .prop('checked', false)
                            .prop('selected', false);
